try {
    const globalDeviceList = {};
    const name = msg.payload.name.toLowerCase()
    globalDeviceList[name] = msg.payload

    const newList = {
        ...global.get("globalDeviceList") || {},
        ...globalDeviceList
    }


    global.set("globalDeviceList", newList);
    return { msg, deviceList: newList };
} catch (err) {
    node.error(err)
    return {msg, err}
}

function escapeRegExp(string) {
    return string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'); // $& means the whole matched string
}

function replaceAll(str, find, replace) {
    return str.replace(new RegExp(escapeRegExp(find), 'g'), replace);
}

const trimCommandString = (id) => ((id === 'all')
    ? '' : replaceAll(id.replace(/the/g, '').trim().toLowerCase(), '_', ' '));

const toggle = (isOn) => (isOn ? "off" : "on")

const getIsOn = (deviceInfo, type) => ((type === 'group') ? deviceInfo.isOn : deviceInfo[type].isOn)

function findAndToggle (input) {
    const device = trimCommandString(
        input.req.params.device.toLowerCase()
    )
    const deviceList = global.get("globalDeviceList")
    const deviceInfo = deviceList[device];
    // node.warn(deviceList);
    // node.warn(deviceInfo);

    const type = deviceInfo.type
    const isOn = getIsOn(deviceInfo, type)

    return {
        topic: deviceInfo.instanceId,
        payload: toggle(isOn),
        res: input.res
    };
}

try {
    return findAndToggle(msg);
} catch (err) {
    node.error(err)
    return {
        payload: err.message,
        res: msg.res,
        statusCode: 500
    }
}

const formatGroups = (devices) => Object.keys(devices).reduce(
    (prev, key) =>
    ((devices[key].type === 'group' && devices[key].name !== 'SuperGroup') ? [
        ...(prev || []),
        {
            key,
            name: devices[key].name,
            type: devices[key].type,
            instanceId: devices[key].instanceId,
            alive: devices[key].alive,
            state: devices[key][devices[key].type] || { isOn: devices[key].isOn },
            deviceIds: devices[key].deviceIds,
        }]
        : prev),
    [],
);

const formatDevices = (devices) => Object.keys(devices).reduce((prev, key) => ({
    ...prev,
    [devices[key].instanceId]: {
        key,
        name: devices[key].name,
        type: devices[key].type,
        instanceId: devices[key].instanceId,
        alive: devices[key].alive,
        state: devices[key][devices[key].type] || { isOn: devices[key].isOn },
        deviceIds: devices[key].deviceIds,
    }
}), {});

const categorizeByDeviceType = (devices) =>
    devices.reduce((prev, curr) => ({
        ...prev,
        [curr.type]: [
            ...(prev[curr.type] || []),
            curr,
        ],
    }), {});

const categorizeByGroup = (groups, devices) =>
    groups.map((group) => ({
        ...group,
        devices: group.deviceIds.reduce(
            (prev, deviceId) =>
            ((devices[deviceId] && ['lightbulb', 'plug'].includes(devices[deviceId].type) ) ? [
                ...prev,
                devices[deviceId],
            ] : prev),
            [],
        ),
    }));

function getAndSortAllDevices() {
    const devices = global.get('globalDeviceList');
    node.warn(devices)
    const groups = formatGroups(devices);
    const formattedDevices = formatDevices(devices);

    // const categorizedDevices = categorizeByDeviceType(formattedDevices)
    return categorizeByGroup(groups, formattedDevices);
}

return {
    payload: getAndSortAllDevices(),
    res: msg.res
};

