const data = msg.payload.toString();

const colorizeText = (string) => 
    string.replace('[0;32m', '<span style="color: #449e48;">')
        .replace('[0;31m', '<span class="mui--text-danger">')
        .replace('[0m', '</span>')

const splitData = data.split(/\r?\n/);

const formatted = splitData.map((row) => {
    const textClass = row.includes('[✗]') ? 'mui--text-danger' : 'mui--text-light'
    return { message: colorizeText(row), textClass}
})
return {...msg, formatted};
